import { useEffect, useState } from "react";
import { BoardModel } from "../../../Models/BoardModel";
import { boardService } from "../../../Services/BoardService";
import { Grid, Layout, Plus, MoreHorizontal } from "lucide-react";
import "./Home.css";

function getColumnColor(columnName: string): string {
  const colors = {
    'Backlog': '#9CA3AF',
    'To Do': '#60A5FA',
    'In Progress': '#F59E0B',
    'Done': '#10B981'
  };

  return colors[columnName] || '#9CA3AF';
}

export function Home(): JSX.Element {
  const [boards, setBoards] = useState<BoardModel[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedBoardId, setSelectedBoardId] = useState<string | null>(null);

  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        const fetchedBoards = await boardService.getAllBoards();
        setBoards(fetchedBoards);
        if (fetchedBoards.length > 0) {
          setSelectedBoardId(fetchedBoards[0]._id);
        }
        setError(null);
      } catch (err) {
        setError("Failed to fetch boards. Please try again later.");
        console.error("Error fetching boards:", err);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <span>Loading your boards...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-message">
        <span>{error}</span>
      </div>
    );
  }

  return (
    <div className="Home">
      <div className="boards-nav">
        <div className="nav-left">
          <h1 className="title">My Boards</h1>
          <div className="view-controls">
            <button className="view-button active">
              <Grid size={20} />
              <span>Grid</span>
            </button>
            <button className="view-button">
              <Layout size={20} />
              <span>List</span>
            </button>
          </div>
        </div>
        <button className="create-board-button">
          <Plus size={20} />
          <span>Create Board</span>
        </button>
      </div>

      <div className="boards-menu">
        {boards.map(board => (
          <button
            key={board._id}
            className={`board-nav-item ${board._id === selectedBoardId ? 'active' : ''}`}
            onClick={() => setSelectedBoardId(board._id)}
          >
            {board.name}
          </button>
        ))}
      </div>

      <div className="boards-grid">
        {boards.map((board) => (
          <div key={board._id} className="board-card">
            <div className="board-header">
              <div className="board-header-left">
                <div className="board-icon">
                  {board.name.charAt(0).toUpperCase()}
                </div>
                <h2 className="board-title">{board.name}</h2>
              </div>
              <button className="board-menu-button">
                <MoreHorizontal size={20} />
              </button>
            </div>

            <div className="board-stats">
              <div className="stat-item">
                <span className="stat-label">Columns</span>
                <span className="stat-value">{board.columns.length}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Members</span>
                <span className="stat-value">{board.members.length}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Created</span>
                <span className="stat-value">
                  {new Date(board.createdAt).toLocaleDateString()}
                </span>
              </div>
            </div>

            <div className="board-content">
              <h3 className="columns-title">Workflow</h3>
              <div className="columns-grid">
                {board.columns
                  .sort((a, b) => a.order - b.order)
                  .map((column, index) => (
                    <div
                      key={index}
                      className="column-item"
                    >
                      <div className="column-color-indicator"
                        style={{
                          backgroundColor: getColumnColor(column.name)
                        }}
                      />
                      <div className="column-details">
                        <span className="column-name">{column.name}</span>
                        <span className="column-order">#{column.order + 1}</span>
                      </div>
                    </div>
                  ))}
              </div>
            </div>

            <div className="board-footer">
              <button className="board-action-button primary">
                Open Board
              </button>
              <button className="board-action-button secondary">
                Settings
              </button>
            </div>
          </div>
        ))}
      </div>

      {boards.length === 0 && (
        <div className="empty-state">
          <div className="empty-icon">📋</div>
          <h3>No Boards Found</h3>
          <p>Create your first board to get started with your projects!</p>
          <button className="create-board-button">
            <Plus size={20} />
            <span>Create Board</span>
          </button>
        </div>
      )}
    </div>
  );
}
