import { useEffect, useState } from "react";
import "./Tasks.css";
import { TaskModel } from "../../../Models/TaskModel";
import { taskService } from "../../../Services/TaskService";
import { Tag } from "lucide-react";

interface TasksProps {
    boardId: string;
    columnId: string;
}

export function Tasks({ boardId, columnId }: TasksProps): JSX.Element {
    const [tasks, setTasks] = useState<TaskModel[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        (async () => {
            try {
                setLoading(true);
                console.log("Board id: " + boardId);
                console.log("Column id: " + columnId);
                const fetchedTasks = await taskService.getTasksByColumn(boardId, columnId);
                console.log(fetchedTasks);
                setTasks(fetchedTasks);
                setError(null);
            } catch (err) {
                console.error("Error fetching tasks:", err);
                setError("Failed to load tasks");
            } finally {
                setLoading(false);
            }
        })();
    }, [boardId, columnId]);

    if (loading) {
        return <div className="tasks-loading">Loading tasks...</div>;
    }

    if (error) {
        return <div className="tasks-error">{error}</div>;
    }

    return (
        <div className="Tasks">
            {tasks.map(task => (
                <div key={task._id} className="task-card">

                    {/* Labels */}
                    {task.labels.length > 0 && (
                        <div className="task-labels">
                            {task.labels.map((label, index) => (
                                <span key={index} className="task-label">
                                    <Tag className="label-icon" />
                                    {label}
                                </span>
                            ))}
                        </div>
                    )}

                    {/* Title */}
                    <h3 className="task-title">{task.title}</h3>

                    {/* Description preview */}
                    {task.description && (
                        <p className="task-description">
                            {task.description}
                        </p>
                    )}

                    {/* Priority badge */}
                    <span className={`task-priority task-priority-${task.priority}`}>
                        {task.priority.charAt(0).toUpperCase() + task.priority.slice(1)} Priority
                    </span>
                    <div className="task-bottom">
                        {task.dueDate && (
                            <span className="task-due-date">
                                Due: {new Date(task.dueDate).toLocaleDateString()}
                            </span>
                        )}

                        {task.assignees.length > 0 && (
                            <div className="task-assignees">
                                Assignees: {task.assignees.length}
                            </div>
                        )}

                        {task.attachments.length > 0 &&
                            <div className="task-attachments"></div>
                        }
                    </div>

                </div>
            ))}
            {tasks.length === 0 && (
                <div className="no-tasks">No tasks in this column</div>
            )}
        </div>
    );
}
