// Home.tsx
import { useEffect, useState } from "react";
import "./Home.css";
import { boardService } from "../../../Services/BoardService";
import { BoardModel } from "../../../Models/BoardModel";
import { Plus, MoreHorizontal } from "lucide-react";

interface TaskModel {
    _id: string;
    title: string;
    columnName: string;
    dueDate?: Date;
    assignedTo?: string;
    // Add other task properties as needed
}

interface ColumnViewModel {
    name: string;
    order: number;
    tasks: TaskModel[];
}

export function Home(): JSX.Element {
    const [board, setBoard] = useState<BoardModel | null>(null);
    const [columns, setColumns] = useState<ColumnViewModel[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function fetchBoard() {
            try {
                const boards = await boardService.getAllBoards();
                if (boards && boards.length > 0) {
                    const currentBoard = boards[0]; // Get first board for now
                    setBoard(currentBoard);

                    // Sort columns by order
                    const sortedColumns = [...currentBoard.columns].sort((a, b) => a.order - b.order);

                    // Transform to ColumnViewModel
                    const columnsWithTasks = sortedColumns.map(col => ({
                        name: col.name,
                        order: col.order,
                        tasks: [] // You'll need to fetch tasks separately
                    }));

                    setColumns(columnsWithTasks);
                }
            } catch (error) {
                console.error("Failed to fetch board:", error);
            } finally {
                setLoading(false);
            }
        }
        fetchBoard();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="Home">
            {board && (
                <div className="board-header">
                    <h1>{board.name}</h1>
                </div>
            )}

            <div className="board-wrapper">
                {columns.map((column, index) => (
                    <div key={`${column.name}-${column.order}`} className="board-column">
                        <div className="column-header">
                            <div className="column-title">
                                <div className={`column-icon ${column.name.toLowerCase().replace(' ', '-')}`} />
                                <h2>{column.name}</h2>
                                <span className="task-count">{column.tasks.length}</span>
                            </div>
                            <div className="column-actions">
                                <button className="icon-button">
                                    <MoreHorizontal size={20} />
                                </button>
                                <button className="icon-button">
                                    <Plus size={20} />
                                </button>
                            </div>
                        </div>

                        <div className="tasks-container">
                            {/* Placeholder card - replace with actual tasks */}
                            <div className="task-card">
                                <div className="task-header">
                                    <span className="task-category">Task</span>
                                    <div className="task-avatar">
                                        <img src="/api/placeholder/32/32" alt="User" />
                                    </div>
                                </div>
                                <h3 className="task-title">Sample Task</h3>
                                <div className="task-metrics">
                                    <span className="metric">0</span>
                                    <span className="metric">0</span>
                                    <span className="metric">No due date</span>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
