import React, { useEffect, useState } from "react";
import { boardService } from "../../../Services/BoardService";
import { BoardModel } from "../../../Models/BoardModel";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";

const Home: React.FC = () => {
  const [boards, setBoards] = useState<BoardModel[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchBoards = async () => {
      try {
        setLoading(true);
        const fetchedBoards = await boardService.getAllBoards();
        setBoards(fetchedBoards);
        setError(null);
      } catch (err) {
        setError("Failed to fetch boards. Please try again later.");
        console.error("Error fetching boards:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchBoards();
  }, []);

  if (loading) {
    return <div className="flex justify-center items-center h-64">Loading...</div>;
  }

  if (error) {
    return (
      <Alert variant="destructive">
        <AlertDescription>{error}</AlertDescription>
      </Alert>
    );
  }

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">My Boards</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {boards.map((board) => (
          <Card key={board._id} className="hover:shadow-lg transition-shadow">
            <CardHeader>
              <CardTitle>{board.name}</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-2">
                <h3 className="font-semibold">Columns:</h3>
                <div className="grid grid-cols-2 gap-2">
                  {board.columns
                    .sort((a, b) => a.order - b.order)
                    .map((column, index) => (
                      <div
                        key={index}
                        className="bg-gray-100 p-2 rounded"
                      >
                        <span className="font-medium">{column.name}</span>
                        <span className="text-gray-500 text-sm ml-2">
                          (Order: {column.order})
                        </span>
                      </div>
                    ))}
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {boards.length === 0 && (
        <div className="text-center text-gray-500 mt-8">
          No boards found. Create your first board to get started!
        </div>
      )}
    </div>
  );
};

export default Home;
